{"version":"KqlItem/1.0","query":"let data_disk_threshold_percent = 10;
let os_disk_threshold_percent = 10;
let disk_free_space_percent=
Perf 
| where TimeGenerated > ago(10m)
| where ObjectName == "LogicalDisk" and CounterName == "% Free Space" 
| summarize (TimeGenerated, Free_Space_Percent)=arg_max(TimeGenerated, CounterValue) by Computer, InstanceName 
| where strlen(InstanceName) ==2 and InstanceName contains ":";
let disk_free_MB=
Perf 
| where TimeGenerated > ago(10m)
| where ObjectName == "LogicalDisk" and CounterName == "Free Megabytes" 
| summarize (TimeGenerated, Free_MB)=arg_max(TimeGenerated, CounterValue) by Computer, InstanceName 
| where strlen(InstanceName) ==2 and InstanceName contains ":";
disk_free_space_percent 
| join (
   disk_free_MB 
) on Computer, InstanceName
| extend Used_Space_Percent = round(100 - Free_Space_Percent)
| extend Alert_Threshold = case(InstanceName == 'C:', os_disk_threshold_percent, data_disk_threshold_percent)
| where (InstanceName in ('C:', 'D:') and Free_Space_Percent <= os_disk_threshold_percent) 
        or (InstanceName !in ('C:', 'D:') and Free_Space_Percent <= data_disk_threshold_percent)
| parse Computer with Computer "." *
| project Server = Computer, Drive = InstanceName, UsedSpacePCT = Used_Space_Percent, FreeSpacePCT = Free_Space_Percent , Free_MB
//| project Server = Computer, InstanceName, UsedSpacePCT = Used_Space_Percent, FreeSpacePCT = Free_Space_Percent , Free_MB
//| where ((InstanceName !in ("C:","D:","Z:","S:") and Used_Space_Percent > 90)
| where ((Drive !contains "C:" and Drive !contains "D:" and Drive !contains "S:") and UsedSpacePCT > 90) //or ((InstanceName <> "C:" and InstanceName <> "D:") and Used_Space_Percent >= 95)
| where (Server contains "db") or (Server in~ ("azsapd003","azsapd007","azsapd035","azsapd039","azsapd041","azsapd044","dkcdcsapp076","dkcdcsapd082"))","size":1,"title":"Windows Critical DIsk Space","noDataMessage":"No critical disk space detected.","timeContext":{"durationMs":86400000},"showRefreshButton":true,"queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["/subscriptions/cf49a21b-2b75-4240-b62b-2f5b04f53563/resourceGroups/dch-coremgmt-rg/providers/Microsoft.OperationalInsights/workspaces/weu-prod-dch-log01"],"gridSettings":{"formatters":[{"columnMatch":"UsedSpacePCT","formatter":3,"formatOptions":{"min":1,"max":100,"palette":"greenRed"}},{"columnMatch":"Used_Space_Percent","formatter":3,"formatOptions":{"min":1,"max":100,"palette":"greenRed"}}]}}

{"version":"KqlItem/1.0","query":"// function Params - Report Filters
let RangeStart = startofday({TimeRange:start});
let RangeEnd = iff(startofday({TimeRange:end}) == startofday(now()) ,startofday({TimeRange:end}) - 1d , startofday({TimeRange:end}));
let VaultSubscriptionList = todynamic( replace("/subscriptions/", "", @"{VaultSubscription}"));
let VaultLocationList = todynamic( @"{VaultLocation}"); 
let VaultTypeList = "*";
let VaultList = todynamic( @"{VaultName}");
let ExcludeLegacyEvent = @"{ExcludeLegacyEvent}";
let BackupSolutionList = @"{DatasourceType}";
// function Params - other Filters
let ProtectionInfoList =  "*";
let Item_search =   "*;*";
let ItemArray = split(Item_search, ";");
let ItemArray_length = array_length(ItemArray);
let BackupInstanceName = iff(ItemArray_length == 2, ItemArray[1], ItemArray[0] );
let DatasourceSetName = iff(ItemArray_length == 2, ItemArray[0], "");
let DisplayAllFields = true;
let JobOperationList = "*";
let JobStatusList =  "*";
let JobFailureCodeList =  "*";
let ExcludeLog = True;
let BackupInstanceDetails = (){
// calling LA System Function
_AzureBackup_GetBackupInstances(RangeEnd, RangeEnd, VaultSubscriptionList, VaultLocationList, VaultList, VaultTypeList, ExcludeLegacyEvent, BackupSolutionList, ProtectionInfoList, DatasourceSetName, BackupInstanceName, DisplayAllFields)
// query to transform function output
| summarize BackupInstances=count(UniqueId), CloudStorage=sum(VaultStore_StorageConsumptionInMBs)/(1024) by BackupSolution
};
let JobSuccessPercentageTable = (){
// calling LA System Function
_AzureBackup_GetJobs(RangeStart, RangeEnd, VaultSubscriptionList, VaultLocationList, VaultList, VaultTypeList, ExcludeLegacyEvent, BackupSolutionList, JobOperationList, JobStatusList, JobFailureCodeList, DatasourceSetName, BackupInstanceName, ExcludeLog)
// query to transform function output
| extend success=iff((Status == "Completed" or Status == "CompletedWithWarnings") , 1 , 0)
| extend record = iff(UniqueId != "", 1, 0)  // to be used for total row count
| summarize SuccessfulCount =sum(success), TotalCount=sum(record) by BackupSolution
| extend JobSuccessPercentage = (SuccessfulCount*100)/todouble(TotalCount)
| project-away SuccessfulCount, TotalCount
};
BackupInstanceDetails | join kind = leftouter (JobSuccessPercentageTable) on BackupSolution  | project-away BackupSolution1 | project BackupSolution, BackupInstances, JobSuccessPercentage=iff( (isnan( JobSuccessPercentage) or isinf( JobSuccessPercentage) or isempty( JobSuccessPercentage)), "-", strcat(tostring(round( JobSuccessPercentage,2)), " %")), CloudStorage","size":3,"noDataMessage":"No records found for the selected time range and scope","showExportToExcel":true,"queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{Workspaces}"],"gridSettings":{"formatters":[{"columnMatch":"BackupSolution","formatter":0,"tooltipFormat":{"tooltip":"Backup Solution"}},{"columnMatch":"BackupInstances","formatter":4,"formatOptions":{"min":0,"palette":"blue","customColumnWidthSetting":"25%"},"tooltipFormat":{"tooltip":"Number of backup instances backed up using the given backup solution, as of the end of the selected time range"}},{"columnMatch":"JobSuccessPercentage","formatter":1,"formatOptions":{"customColumnWidthSetting":"25%"},"numberFormat":{"unit":1,"options":{"style":"decimal","useGrouping":false,"maximumFractionDigits":2}},"tooltipFormat":{"tooltip":"% of Jobs (that were triggered in the selected time range) that were successful for backup instances backed up using the given backup solution"}},{"columnMatch":"CloudStorage","formatter":4,"formatOptions":{"min":0,"palette":"blue","customColumnWidthSetting":"25%"},"numberFormat":{"unit":0,"options":{"style":"decimal","minimumFractionDigits":2,"maximumFractionDigits":2}},"tooltipFormat":{"tooltip":"Total cloud storage consumed by backup instances backed up by the given backup solution, as of the end of the selected time range"}}],"labelSettings":[{"columnId":"BackupSolution","label":"Backup Solution"},{"columnId":"BackupInstances","label":"Backup Instances"},{"columnId":"JobSuccessPercentage","label":"Job Success %"},{"columnId":"CloudStorage","label":"Cloud Storage (GB)"}]},"sortBy":[]}

